import React, {useState, useContext} from 'react';
import {Slider, Label, Result, Container, ContainerLabels} from './SliderStyled.js';
import {useForm} from "react-hook-form";


const SliderComponent = ({type, setWeight}) => {
    const [rangeval1, setRangeval1] = useState(100);
    let props =  rangeval1;
    const [rangeval2, setRangeval2] = useState(1.50);
    if(type === "peso")      
        return (  
            props,
            <Container>
                <ContainerLabels>
                        <Label>Peso</Label>
                        <Result>{rangeval1}</Result>                                                         
                </ContainerLabels>
                <Slider onChange={(event) => {setRangeval1(event.target.value); setWeight(event.target.value);}} type = "range" min={1} max={250} defaultValue={100} step="1" onChange={(event) => setRangeval1(event.target.value)}/>             
            </Container> 
        );
    if(type === "altura")      
        return (  
            <Container>
                <ContainerLabels>
                        <Label>Altura</Label>
                        <Result>{rangeval2}</Result>                                                         
                </ContainerLabels>
                <Slider type = "range" min={1.00} max={2.50} defaultValue={1.50} step="0.01" onChange={(event) => setRangeval2(event.target.value)}/>                
            </Container> 
        );    
        
}

export default SliderComponent;
