import styled from 'styled-components';

export const Button = styled.button`
    color: #FFFFFF;
    font-family: "poppins";
    background-color: #F7B718;
    font-size: 30px;
    width: 350px;
    height: 60px;
    border: none;
    outline-style: none;
    border-radius: 4px;
`;
export const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items:center;
    width: 100%;
`;

export const Result = styled.p`
    color: #000000;
    font-family: "poppins";
    font-size: 30px;
`;
export const ResultContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items:center;
    width: 100%;
    height: 10%;
`;
