import React from 'react';
import {Main, Card, Result} from './AppStyled.jsx';
import TitleComponent from '../components/Title/Title.jsx';
import SliderComponent from '../components/Slider/Slider.jsx';
import ButtonComponent from '../components/Button/Button.jsx';


const App = ({rangeval1, rangeval2}) => {
  let result = null;

  const Calculate = () => {
    result = rangeval1 / Math.pow(rangeval2, 2);
  }

  if(result !== 0 || result != null) {
    return (
      <Main>
        <Card>
          <TitleComponent/> 
          <SliderComponent type = "peso"/>
          <SliderComponent type = "altura"/>
          <ButtonComponent onclick={Calculate()}/>
          <Result>{result}</Result>
        </Card>
      </Main>
    );
  } else {
    return (
      <Main>
        <Card>
          <TitleComponent/> 
          <SliderComponent type = "peso"/>
          <SliderComponent type = "altura"/>
          <ButtonComponent onclick={Calculate()}/>
        </Card>
      </Main>
    );
  }
}

export default App;
