import React, {useState} from 'react';
import {Slider, Label, Result, Container} from './SliderStyled.js';

const SliderComponent = ({type}) => {
    
    const [rangeval, setRangeval] = useState(200);

    if(type === "peso")      
        return (  
            <Container>
                <Label>Peso</Label>
                <Result>{rangeval}</Result>
                <Slider type = "range"  minValue={0} maxValue={200} defaultValue={200} onChange={(event) => setRangeval(event.target.value)}/>
                
            </Container> 
        );
    
        
}

export default SliderComponent;
