import React from 'react';
import {Slider, Label, Result} from './SliderStyled.js';

const SliderComponent = ({type}) => {
    
    if(type === "peso") 
      
        return (  
            <>
            <Label>Peso</Label>
            <Slider type = "range" defaultValue={100} maxValue={200} minValue={0} value={this.state.value} onChange={(values) => this.setState({ values })}/>
            <Result>{Slider.value}</Result>
            </> 
        );
    
        
}

export default SliderComponent;
