import React, {useContext, useState} from 'react';
import { useForm } from "react-hook-form";
import {Main, Card, Result} from './AppStyled.js';
import TitleComponent from '../components/Title/Title.jsx';
import SliderComponent from '../components/Slider/Slider.jsx';
import ButtonComponent from '../components/Button/Button.jsx';
import ResultComponent from '../components/Result/Result.jsx';


const App = () => {

  const [weight, setWeight] = useState(0);
  const [hight, setHight] = useState(0);
  const[result, setResult] = useState(0);

  const changeResult = () => {
    setResult((weight / Math.pow(hight, 2)))
  }

  const {handleSubmit } = useForm();


  return (
      <Main>
      <Card onSubmit={handleSubmit(changeResult)}>
        <TitleComponent/> 
        <SliderComponent setWeight = {setWeight} type = "peso"/>
        <SliderComponent setHight = {setHight} type = "altura"/>       
        <ButtonComponent/>
        <ResultComponent label = {result}/>
      </Card>
    </Main>
  ); 
}

export default App;
