import React, {useState} from 'react';
import {Slider, Label, Result, Container, ContainerLabels} from './SliderStyled.js';

const SliderComponent = ({type}) => {

    const [rangeval1, setRangeval1] = useState(100);
    const [rangeval2, setRangeval2] = useState(1.00);

    if(type === "peso")      
        return (  
            <Container>
                <ContainerLabels>
                        <Label>Peso (kg)</Label>
                        <Result>{rangeval1}</Result>                                                         
                </ContainerLabels>
                <Slider type = "range" min={1} max={250} defaultValue={100} step="1" onChange={(event) => setRangeval1(event.target.value)}/>
                
            </Container> 
        );
    if(type === "altura")      
        return (  
            <Container>
                <ContainerLabels>
                        <Label>Altura (cm)</Label>
                        <Result>{rangeval2}</Result>                                                         
                </ContainerLabels>
                <Slider type = "range"  min={1.0} max={2.50} defaultValue={1.50} step="0.1" onChange={(event) => setRangeval2(event.target.value)}/>                
            </Container> 
        );    
        
}

export default SliderComponent;
